import React, { Component } from 'react';
import PropTypes from 'prop-types';

export class reservationitem extends Component {
   
    getStyle = () => {
        return {
            background: '#f4f4f4',
            padding: '10px',
            borderBottom: '1px #ccc dotted',
            textDecoration: this.props.reservation.completed ? 'line=through': 'none'
        }
    }


   
    render() {

        const { id, CustName, Date, OrderMenu} = this.props.reservation;
        return (
            <div style ={this.getStyle()}>
                
                       <table>
                           <th>Name</th>
                           <td>{CustName}</td>
                           <th>Date</th>
                           <td> {Date} </td>
                           <th>Order</th>
                           <td>  {OrderMenu}  </td>
                         
                        </table>
                        <button onClick={this.props.delReservation.bind(this, id)} style={btnStyle}>x</button>
                     
                        <table>
  <tr>
    <th>Name</th>
    <th>Date</th>
    <th>Order</th>
  </tr>
  <tr>
    <td>{CustName}</td>
    <td>{Date}</td>
    <td>{OrderMenu} </td>
  </tr>

  </table>      
                    
            </div>
        )
    }
}
reservationitem.propTypes = {
    reservation: PropTypes.object.isRequired
}

const btnStyle = {
    background: 'red',
    color  : 'white',
    border: 'none',
    padding: '3px 6px',
    borderRadius: '50%',
    cursor: 'pointer',
    float: 'right'
}


export default reservationitem
