import React, { Component } from 'react';
import PropTypes from 'prop-types';

export class reservationitem extends Component {
   
    getStyle = () => {
        return {
            background: '#f4f4f4',
            padding: '10px',
           
          
        }
    }


   
    render() {

        const { id, CustName, Date, OrderMenu} = this.props.reservation;
        return (
            <div style ={this.getStyle()}>
                
                            <table style ={tableStyle}>
                            <tr style ={tableStyle}>
                                <th style ={tableStyle}>Name</th>
                                <th style ={tableStyle}>Date</th>
                                <th style ={tableStyle}>Order</th>
                            </tr>
                            <tr style ={tableStyle}>
                                <td>{CustName}</td>
                                <td>{Date}</td>
                                <td>{OrderMenu} </td>
                                
                            </tr>
                            </table>    
                            <button onClick={this.props.delReservation.bind(this, id)} style={btnStyle}>x</button>
                       
                           
  
                    
            </div>
        )
    }
}
reservationitem.propTypes = {
    reservation: PropTypes.object.isRequired
}

const btnStyle = {
    background: 'black',
    color  : 'white',
    border: 'none',
    padding: '3px 6px',
    cursor: 'pointer',
    float: 'right'
  
    
}

const tableStyle = {
    border : ' 1px solid black'
}


export default reservationitem
