import React, { Component } from 'react';
import PropTypes from 'prop-types';

export class reservationitem extends Component {
   
    getStyle = () => {
        return {
            background: '#f4f4f4',
            padding: '10px',
            borderBottom: '1px #ccc dotted',
            textDecoration: this.props.reservation.completed ? 'line=through': 'none'
        }
    }


   
    render() {

        const {id, CustName, Date, OrderMenu} = this.props.reservation;
        return (
            <div style ={this.getStyle()}>
                <p>
                        <input type="checkbox" onChange={this.props.markComplete.bind(this, this.props.reservation.id)}/> {'  '} 
                        {this.props.reservation.CustName} {this.props.reservation.Date}   {this.props.reservation.OrderMenu} 
                     
               </p>
                    
            </div>
        )
    }
}
reservationitem.propTypes = {
    reservation: PropTypes.object.isRequired
}




export default reservationitem
